import React, { Component } from 'react';
import TodoListInput from "./TodoListInput"
import axios from 'axios';

class App extends Component {
  state = {
    todoList: []
  };

  componentDidMount() {
    this.getTodoList();
  }

  getTodoList = () => {
    fetch('http://localhost:3001/api/todoList')
      .then((data) => data.json())
      .then((res) => this.setState({ todoList: res.data }));
  };

  createTodoItem = (message, dueDate) => {
    axios.post('http://localhost:3001/api/todoItem', {
      message: message,
      dueDate: dueDate
    }).then((res) => {
        this.setState({ todoList: this.state.todoList.concat(res.data.todoItem) })
      })
  };

  render() {
    const { todoList } = this.state;
    return (
      <div>
        <h1>Task List</h1>
        {todoList.length === 0  ? (
          <h3> You have no tasks to complete </h3>
        ) : (
          <ul>
            {todoList.map(todoItem => {
              return(
                <li>
                  {todoItem.message} -- {todoItem.dueDate}
                </li>
              )
            })}
          </ul>
        )}
        <TodoForm />
      </div>
    );
  }
}

export default App;
