import axios from "axios";
import React, { Component, Fragment } from "react";
import setAuthToken from "../utils/SetAuthToken";

const navBarStyles = {
  display: "flex",
  flexDirection: "row",
  background: "#f2f6f9",
  padding: "0 40px",
  margin: "0 auto"
}

const userInfoStyles = {
  alignItems: "baseline",
  placeContent: "flex-end",
  display: "flex",
  flexDirection: "row",
}

class Navbar extends Component {
  constructor() {
    super();
    this.state = {
      currentUser: {}
    };
  }

  componentDidMount() {
    axios
      .get(`http://localhost:3001/api/currentUser?token=${localStorage.token}`)
      .then(res => {
        this.setState({ currentUser: res.data.currentUser });
      });
  }

  logoutUser() {
    setAuthToken();
    window.location.replace("/signin");
  }

  render() {
    return (
      <div style={navBarStyles}>
        <div>
          <h1>Todo List</h1>
        </div>
        {this.state.currentUser && (
          <div style={userInfoStyles}>
            <h3>{this.state.currentUser.name}</h3>
            <button onClick={() => this.logoutUser()} style={{marginLeft: "24px", marginBottom: "2px"}}>Signout</button>
          </div>
        )}
      </div>
    );
  }
}

export default Navbar;
